import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

import {
  MdSearch,
  MdLocationOn,
  MdStarBorder,
  MdGroupWork,
} from 'react-icons/md';
import { FiBox } from 'react-icons/fi';
import { AiOutlineUsergroupAdd } from 'react-icons/ai';

import api from '../../services/api';
import history from '../../services/history';

import {
  Container,
  Head,
  Text1,
  Text2,
  Input,
  Button,
  Profile,
  Avatar,
  Name,
  ProfileText,
  RepoList,
  RepoItem,
} from './styles';

export default function Result({ location }) {
  const [user, setUser] = useState('');
  const [repos, setRepos] = useState([]);

  useEffect(() => {
    setUser(location.state.user.login);
    setRepos(location.state.repos);
  }, [location.state.repos, location.state.user.login]);

  async function handleClick() {
    try {
      const responseUser = await api.get(`/users/${user}`);

      const responseRepos = await api.get(`/users/${user}/repos`);

      history.push({
        pathname: '/result',
        state: {
          user: responseUser.data,
          repos: responseRepos.data,
        }, // your data array of objects
      });
    } catch (err) {
      setUser('');
      console.log(err);
    }
  }

  return (
    <>
      <Head>
        <Link style={{ textDecoration: 'none', color: 'black' }} to="/">
          <Text1>Github</Text1>
          <Text2>Search</Text2>
        </Link>

        <Input
          type="text"
          value={user}
          onChange={e => setUser(e.target.value)}
        />
        <Button onClick={handleClick}>
          <MdSearch color="white" size={30} style={{ marginTop: 5 }} />
        </Button>
      </Head>
      <Container>
        <Profile>
          <Avatar src={location.state.user.avatar_url} />
          <Name>{location.state.user.name}</Name>
          <ProfileText>{location.state.user.login}</ProfileText>
          <p />
          <ProfileText>
            <MdGroupWork size={30} />
            {location.state.user.company}
          </ProfileText>
          <ProfileText>
            <MdLocationOn size={30} />
            {location.state.user.location}
          </ProfileText>
          <ProfileText>
            <MdStarBorder size={30} />
            99999
          </ProfileText>
          <ProfileText>
            <FiBox size={30} />
            {location.state.user.public_repos}
          </ProfileText>
          <ProfileText>
            <AiOutlineUsergroupAdd size={30} />
            {location.state.user.followers}
          </ProfileText>
        </Profile>

        <RepoList>
          <ul>
            {repos.map(repo => (
              <RepoItem key={repo.id}>
                <Link to={repo.html_url}>
                  <Name style={{ color: '#ac53f2' }}>{repo.name}</Name>
                </Link>
                <ProfileText style={{ color: '#000000' }}>
                  {repo.description}
                </ProfileText>
                <ProfileText style={{ marginTop: 10 }}>
                  <MdStarBorder size={30} />
                  {repo.stargazers_count}
                </ProfileText>
              </RepoItem>
            ))}
          </ul>
        </RepoList>
      </Container>
    </>
  );
}
