import React from 'react';

import {
  Container,
  Head,
  Text1,
  Text2,
  Input,
  Button,
  Profile,
  Avatar,
  Name,
  ProfileText,
  RepoList,
  RepoItem,
} from './styles';

export default function NotFound({ location }) {
  const [user, setUser] = useState('');
  const [repos, setRepos] = useState([]);
  const [stars, setStars] = useState(0);

  useEffect(() => {
    setRepos(location.state.repos);
    let count = 0;
    repos.map(repo => {
      count += repo.stargazers_count;
    });
    setStars(count);
    setUser(location.state.user.login);
  }, [location.state.repos, location.state.user.login, repos]);

  async function handleClick() {
    try {
      const responseUser = await api.get(`/users/${user}`);

      const responseRepos = await api.get(`/users/${user}/repos`);

      history.push({
        pathname: '/result',
        state: {
          user: responseUser.data,
          repos: responseRepos.data,
        }, // your data array of objects
      });
    } catch (err) {
      setUser('');
      console.log(err);
    }
  }

  return (
    <>
      <Head>
        <Link style={{ textDecoration: 'none', color: 'black' }} to="/">
          <Text1>Github</Text1>
          <Text2>Search</Text2>
        </Link>

        <Input
          type="text"
          value={user}
          onChange={e => setUser(e.target.value)}
        />
        <Button onClick={handleClick}>
          <MdSearch color="white" size={30} style={{ marginTop: 5 }} />
        </Button>
      </Head>
      <Container />
    </>
  );
}
